mapper.insert:
INSERT INTO
    store_detail
    (
      store_id
    , product_id
    , store_detail_price
    , store_detail_quantity
    )
VALUES
    (
      #{storeId}
    , #{productId}
    , #{storeDetailPrice}
    , 0
    )

mapper.update:
UPDATE
    store_detail sd
        JOIN product p ON p.product_id = sd.product_id
        JOIN store s ON s.store_id = sd.store_id
SET
  , sd.store_detail_id = #{storeDetailId}
  , sd.store_detail_price = #{storeDetailPrice}
  , sd.is_discount = #{isDiscount}
WHERE
    sd.product_id = #{productId}
    AND s.user_id = #{manageId}
