mapper.insert:
INSERT INTO
store_order
(
  warehouse_id
, store_id
, store_order_subject
, store_order_status
, store_order_memo
, shipment_date
)
VALUES
    (
     #{warehouseId}
    ,#{storeId}
    ,#{storeOrderSubject}
    ,#{storeOrderStatus}
    ,#{storeOrderMemo}
    ,#{shipmentDate}
    )

INSERT INTO store_order_detail
(
  store_order_id
, product_id
, store_order_detail_quantity
)
VALUES
    (
     #{storeOrderId}
    ,#{productId}
    ,#{storeOrderDetailQuantity}
    )

mapper.update:
UPDATE store_order
SET
    store_order_subject = #{storeOrderSubject},
    store_order_memo = #{storeOrderMemo},
    modified_at = #{modifiedAt}
WHERE store_order_id = #{storeOrderId}

UPDATE store_order_detail
SET
    product_id = #{productId},
    store_order_detail_quantity = #{storeOrderDetailQuantity},
    modified_at = #{modifiedAt}
WHERE store_order_detail_id = #{storeOrderDetailId}
and   store_order_id = #{storeOrderId}
